import logo from './logo.svg';
import './App.css';
import React from "react";
import useDynamicRefs from 'use-dynamic-refs';
import Ripples from 'react-ripples'

const _ = require('lodash');

let bingo = [
  "(child noises in the background)",
  "Hello, hello?",
  "i need to jump in another call",
  "can everyone go on mute",
  "could you please get closer to the mic",
  "(load painful echo / feedback)",
  "Next slide, please.",
  "can we take this offline?",
  "is ___ on the call?",
  "Could you share this slides afterwards?",
  "can somebody grant presenter rights?",
  "can you email that to everyone?",
  "sorry, i had problems loging in",
  "(animal noises in the background)",
  "Sorry, i didn’t found the conference id",
  "i was having connection issues",
  "i’ll have to get back to you",
  "who just joined?",
  "sorry, somehting ___ with my calenar",
  "do you see my screen?",
  "lets wait for ___!",
  "You will send the minutes?",
  "sorry was on the mute.",
  "can you repeat please?"
]

function App() {
  let [state, setState] = React.useState({viewPort: null});
  let [gameObjects, setGameObjects] = React.useState([]);
  let homeDiv = React.useRef(null);
  let squareDiv = React.useRef(bingo.map(() => React.createRef()));
  const [getRef, setRef] =  useDynamicRefs();


  function createGame(){
    console.log("Game Created")
    //Firstly shuffling the array
    bingo = bingo
        .map(value => ({ value, sort: Math.random() }))
        .sort((a, b) => a.sort - b.sort)
        .map(({ value }) => value)

    //Then creating objects and adding to state
    let objects = [];
    for(let i=0; i<bingo.length; i++){
      if(i == 12) {
        //12. Element is free BINGO
        objects.push({name: "BINGO!", checked: true, correct: false, bingo: true})
      }
      objects.push({name: bingo[i], checked: false, correct: false, bingo: false})
    }
    setGameObjects(objects);
  }

  const clicked  = (num) => {
    let obj = gameObjects;
    console.log("Clicked: " +num + " -> " + JSON.stringify(obj[num]));

    obj[num].checked = !obj[num].checked;

    setGameObjects(obj);
    setGameObjects([...gameObjects]);
    resetCorrect();
    checkRow();
    checkColumn();
  }

  function resetCorrect(){
    let obj = gameObjects;
    for(let i=0; i<25; i++){
      obj[i].correct = false;
    }
    setGameObjects(obj);
    setGameObjects([...gameObjects]);
  }
  function checkRow(){
    let obj = gameObjects;
    for(let i=0; i<5; i++){
      let completed = true;
      for(let j=i*5; j<(i*5)+5; j++){
        if(!obj[j].checked){
          completed = false;
        }
      }
      for(let j=i*5; j<(i*5)+5; j++){
        if(completed){
          obj[j].correct = true;
        }
      }
      setGameObjects(obj);
      setGameObjects([...gameObjects]);
    }
  }
  function checkColumn(){
    let obj = gameObjects;
    for(let i=0; i<5; i++){
      let completed = true;
      for(let j=i; j<(i+21); j+=5){
        if(!obj[j].checked){
          completed = false;
        }
      }
      for(let j=i; j<(i+21); j+=5){
        if(completed){
          obj[j].correct = true;
        }
      }
    }
    setGameObjects(obj);
    setGameObjects([...gameObjects]);
  }

  React.useEffect(() => {
    createGame();
  }, [])

  React.useEffect(() => {
    const reportWindowSize = _.throttle(() => {
      console.log(`Window size: ${window.innerWidth} x ${window.innerHeight}`);
      setState({viewPort: {width: window.innerWidth, height: window.innerHeight}});
    }, 100);

    reportWindowSize();
    window.addEventListener('resize', reportWindowSize);

    return () => {window.removeEventListener('resize', reportWindowSize);}
  }, []);

  React.useEffect(() => {
    if (state.viewPort) {
      let n = Math.min(state.viewPort.height, state.viewPort.width);
      homeDiv.current.style.height = `${n-40}px`;
      homeDiv.current.style.width = `${n-40}px`;
      for(let i=0; i<gameObjects.length; i++){
        const id = getRef("square_"+i);
        if(id.current){
          id.current.style.height = `${(n-40)/5}px`;
          id.current.style.width = `${(n-40)/5}px`;

          let font_size = Math.max(Math.floor(n/60),10);
          if(i == 12){
            font_size += 8;
          }
          id.current.style.fontSize = font_size+'px';
        }
      }
    }
  });

  return (
      <div className="Main">
          <div className="App" ref={homeDiv}>
            <div className="top"></div>
            <div className="right"></div>
            {
              gameObjects.map((item, i) => {
                let itemClass = "Square";
                if(item.bingo){
                  itemClass = "Square Bingo";
                }else{
                  if(item.correct){
                    itemClass = "Square Correct";
                  }else if(item.checked){
                    itemClass = "Square Selected";
                  }
                }
                return (
                  <Ripples key={i}>
                    <button className={itemClass} disabled={item.bingo?true:false} ref={setRef("square_" + i)} onClick={(event) => clicked(i)}>
                      {item.name}
                      <p className="SquareNumber">{i}</p>
                    </button>
                  </Ripples>
                );
              })
            }
          </div>
      </div>
  );
}

export default App;
